// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

contract Example {
    function TimeUnitExample() public {
        // 1秒
        assert(1 seconds == 1);

        // 1分鐘 = 60秒
        assert(1 minutes == 60 seconds);

        // 1小時 = 60分鐘
        assert(1 hours == 60 minutes);

        // 1天=24小時
        assert(1 days == 24 hours);

        // 1周 = 7天
        assert(1 weeks == 7 days);
    }
}
